var assert=require("assert");var utils=require("../test/test-utils").test_utils;var dist=require("../src/ran").dist;var mc=require("../src/ran").mc;describe("ran",function(){describe("mc",function(){describe("Metropolis",function(){describe("burnIn",function(){it("should stop at an acceptance rate of 50%",function(){var e=new mc.Metropolis(new dist.Weibull(5,2).pdf,1);var t=e.burnIn(null,1e3);assert.equal(t.result,.5);assert.equal(t.iterations<1e3,true)});it("should stop at the given number of batches",function(){var e=new mc.Metropolis(new dist.Weibull(5,2).pdf,1);var t=e.burnIn(null,3);assert.notEqual(t.result,.5);assert.equal(t.iterations,3)})})})})});